    desktop: {
      breakpoint: {max: 2000, min: 1280},
      items: 7,
    },
    tablet: {
      breakpoint: {max: 1024, min: 500},
      items: 4,
    },
    min: {
      breakpoint: {max: 500, min: 200},
      items: 2,
    },
  };
